using System;
using System.Collections.Generic;
using System.Linq;
using System.Text;
using Partiality.Modloader;
using UnityEngine;
using OptionalUI;
using System.Reflection;
using System.Reflection.Emit;
using System.IO;
using RWCustom;
using Menu;

public class RainScript : MonoBehaviour
{
    public static Downpour mod;
    public void Initialize()
    {
        RainFall.Patch();
        RainPalette.Patch();
        //Load Snow Palette Image
        Downpour.snowTex = new Texture2D(32, 16, TextureFormat.ARGB32, false);
        Downpour.snowTex.anisoLevel = 0;
        Downpour.snowTex.filterMode = FilterMode.Point;
        byte[] snow = Convert.FromBase64String(Downpour.snowData);
        Downpour.snowTex.LoadImage(snow);
    }
    public void Update()
    {
        if (RainFall.flooding)
        {
            RainFall.floodLevel += 0.15f;
        }
        if (Input.GetKeyDown(KeyCode.Alpha7))
        {
            if (Downpour.snow)
            {
                Downpour.snow = false;
            }
            else
            {
                Downpour.snow = true;
            }
        }
    }
}
public class Downpour : PartialityMod
{
    public Downpour()
    {
        this.ModID = "Downpour";
        this.Version = "v0.5";
        this.author = "LeeMoriya";
    }
    public override void OnEnable()
    {
        base.OnEnable();
        RainScript.mod = this;
        GameObject go = new GameObject();
        script = go.AddComponent<RainScript>();
        script.Initialize();
    }
    public static OptionInterface LoadOI()
    {
        return new DownpourConfig();
    }

    public static RainScript script;
    public static bool paletteChange = true;
    public static bool lightning = true;
    public static bool dynamic = true;
    public static int intensity = 0;
    public static bool rainbow = false;
    public static bool configLoaded = false;
    public static bool debug = true;
    public static bool snow = true;
    public static bool bg = true;
    public static bool water = true;
    public static List<string> rainRegions = new List<string>();
    public static int rainAmount = 50;
    public static int direction = 0;
    public static int rainChance = 75;
    public static bool interiorRain = true;
    public static Texture2D snowTex;
    public static string snowData = "";
}